#ifndef HSF_CS_ENCODE_H
#define HSF_CS_ENCODE_H

#include "objects.h"
#include <string>
#include <stdint.h>
#include <vector>

namespace hsf {
using namespace std;
typedef struct cs_register_object {
  string client_id;
  string data_id;
  string group;
  string addr;
} cs_register_object;

typedef struct cs_publish_object {
  string client_id;
  string data_id;
  string data;
  int64_t revision;
} cs_publish_object;

void cs_register_encode(const cs_register_object &reg_obj, string &output);
void cs_publish_encode(const cs_publish_object &pub_obj, string &output);
void cs_do_publish_encode(const vector<string> &bufs, string &output);

}

#endif
