"use strict";

/**
 * Module dependencies.
 */
var Service = require('./service');

/**
 * Subscribe service addresses from config server
 * @param {Object} options
 *   - {String} configsvr        config server address. Default is dev's 10.232.16.8
 *   - {String} id               data id, such as 'com.taobao.com.nodejs'
 *   - {String} version          version info, such as '1.0.0.daily'
 *   - {String} group            group info, such as 'HSF'
 *   - {Number} routeInterval.   route configSrv get addresses's interval. default 60000 
 * @return {Object} Servie
 */
exports.subService = function (options) {
  return new Service(options);
};
